// LocationsManagement.jsx
import React, { useState } from 'react';
import './locationsmanagement.css';

const LocationsManagement = () => {
  const [locations, setLocations] = useState([
    {
      id: 1,
      nombre: 'Biciestacionamiento Centro Histórico',
      delegacion: 'Cuauhtémoc',
      colonia: 'Centro',
      calle: 'Av. Juárez',
      numero: '50',
      horario: '24 horas',
      capacidad: '30',
      tipoAcceso: 'Público',
      foto: 'https://via.placeholder.com/300x200',
      status: 'active'
    },
    {
      id: 2,
      nombre: 'Estacionamiento Reforma',
      delegacion: 'Cuauhtémoc',
      colonia: 'Juárez',
      calle: 'Paseo de la Reforma',
      numero: '222',
      horario: 'Lunes a Viernes 7:00 - 20:00',
      capacidad: '25',
      tipoAcceso: 'Público',
      foto: 'https://via.placeholder.com/300x200',
      status: 'active'
    }
  ]);

  const [searchTerm, setSearchTerm] = useState('');
  const [selectedLocation, setSelectedLocation] = useState(null);
  const [isEditing, setIsEditing] = useState(false);

  const filteredLocations = locations.filter(location =>
    location.nombre.toLowerCase().includes(searchTerm.toLowerCase()) ||
    location.delegacion.toLowerCase().includes(searchTerm.toLowerCase()) ||
    location.colonia.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleEdit = (location) => {
    setSelectedLocation(location);
    setIsEditing(true);
  };

  const handleDelete = (id) => {
    if (window.confirm('¿Estás seguro de eliminar esta ubicación?')) {
      setLocations(locations.filter(loc => loc.id !== id));
      alert('Ubicación eliminada exitosamente');
    }
  };

  const handleSave = () => {
    setLocations(locations.map(loc =>
      loc.id === selectedLocation.id ? selectedLocation : loc
    ));
    setIsEditing(false);
    setSelectedLocation(null);
    alert('Ubicación actualizada exitosamente');
  };

  return (
    <div className="locations-management">
      <div className="section-header">
        <h2>Gestión de Ubicaciones</h2>
        <span className="badge">{locations.length}</span>
      </div>

      <div className="search-bar-admin">
        <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
          <circle cx="11" cy="11" r="8"></circle>
          <path d="m21 21-4.35-4.35"></path>
        </svg>
        <input
          type="text"
          placeholder="Buscar por nombre, delegación o colonia..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      <div className="locations-grid">
        {filteredLocations.map(location => (
          <div key={location.id} className="location-card">
            <div className="location-image">
              <img src={location.foto} alt={location.nombre} />
              <span className={`status-badge ${location.status}`}>
                {location.status === 'active' ? 'Activo' : 'Inactivo'}
              </span>
            </div>

            <div className="location-info">
              <h3>{location.nombre}</h3>
              <div className="location-detail">
                <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <path d="M21 10c0 7-9 13-9 13s-9-6-9-13a9 9 0 0 1 18 0z"></path>
                  <circle cx="12" cy="10" r="3"></circle>
                </svg>
                <span>{location.calle} {location.numero}, {location.colonia}</span>
              </div>
              <div className="location-detail">
                <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <circle cx="12" cy="12" r="10"></circle>
                  <polyline points="12 6 12 12 16 14"></polyline>
                </svg>
                <span>{location.horario}</span>
              </div>
              <div className="location-detail">
                <svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <rect x="3" y="11" width="18" height="11" rx="2" ry="2"></rect>
                  <path d="M7 11V7a5 5 0 0 1 10 0v4"></path>
                </svg>
                <span>{location.capacidad} espacios - {location.tipoAcceso}</span>
              </div>
            </div>

            <div className="location-actions">
              <button 
                className="btn-edit"
                onClick={() => handleEdit(location)}
              >
                <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <path d="M11 4H4a2 2 0 0 0-2 2v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2v-7"></path>
                  <path d="M18.5 2.5a2.121 2.121 0 0 1 3 3L12 15l-4 1 1-4 9.5-9.5z"></path>
                </svg>
                Editar
              </button>
              <button 
                className="btn-delete"
                onClick={() => handleDelete(location.id)}
              >
                <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <polyline points="3 6 5 6 21 6"></polyline>
                  <path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2"></path>
                </svg>
                Eliminar
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Modal de edición */}
      {isEditing && selectedLocation && (
        <div className="modal-overlay" onClick={() => setIsEditing(false)}>
          <div className="edit-modal" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h2>Editar Ubicación</h2>
              <button 
                className="modal-close"
                onClick={() => setIsEditing(false)}
              >
                <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <line x1="18" y1="6" x2="6" y2="18"></line>
                  <line x1="6" y1="6" x2="18" y2="18"></line>
                </svg>
              </button>
            </div>

            <div className="modal-content">
              <div className="form-group">
                <label>Nombre del Biciestacionamiento</label>
                <input
                  type="text"
                  value={selectedLocation.nombre}
                  onChange={(e) => setSelectedLocation({
                    ...selectedLocation,
                    nombre: e.target.value
                  })}
                />
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Delegación</label>
                  <input
                    type="text"
                    value={selectedLocation.delegacion}
                    onChange={(e) => setSelectedLocation({
                      ...selectedLocation,
                      delegacion: e.target.value
                    })}
                  />
                </div>
                <div className="form-group">
                  <label>Colonia</label>
                  <input
                    type="text"
                    value={selectedLocation.colonia}
                    onChange={(e) => setSelectedLocation({
                      ...selectedLocation,
                      colonia: e.target.value
                    })}
                  />
                </div>
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Calle</label>
                  <input
                    type="text"
                    value={selectedLocation.calle}
                    onChange={(e) => setSelectedLocation({
                      ...selectedLocation,
                      calle: e.target.value
                    })}
                  />
                </div>
                <div className="form-group">
                  <label>Número</label>
                  <input
                    type="text"
                    value={selectedLocation.numero}
                    onChange={(e) => setSelectedLocation({
                      ...selectedLocation,
                      numero: e.target.value
                    })}
                  />
                </div>
              </div>

              <div className="form-group">
                <label>Horario</label>
                <input
                  type="text"
                  value={selectedLocation.horario}
                  onChange={(e) => setSelectedLocation({
                    ...selectedLocation,
                    horario: e.target.value
                  })}
                />
              </div>

              <div className="form-row">
                <div className="form-group">
                  <label>Capacidad</label>
                  <input
                    type="number"
                    value={selectedLocation.capacidad}
                    onChange={(e) => setSelectedLocation({
                      ...selectedLocation,
                      capacidad: e.target.value
                    })}
                  />
                </div>
                <div className="form-group">
                  <label>Tipo de Acceso</label>
                  <select
                    value={selectedLocation.tipoAcceso}
                    onChange={(e) => setSelectedLocation({
                      ...selectedLocation,
                      tipoAcceso: e.target.value
                    })}
                  >
                    <option value="Público">Público</option>
                    <option value="Privado">Privado</option>
                    <option value="Restringido">Restringido</option>
                  </select>
                </div>
              </div>

              <div className="modal-actions">
                <button 
                  className="btn-cancel"
                  onClick={() => setIsEditing(false)}
                >
                  Cancelar
                </button>
                <button 
                  className="btn-save"
                  onClick={handleSave}
                >
                  Guardar Cambios
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default LocationsManagement;
