// Map.jsx - Versión mejorada y segura
import React from 'react';
import { GoogleMap, LoadScript, Marker } from '@react-google-maps/api';
import './map.css';

// Estilos del contenedor del mapa (altura dinámica)
const mapStyles = {
  height: "100%",
  width: "100%"
};

// Coordenadas por defecto (Ciudad de México)
const defaultCenter = {
  lat: 19.404484068754225,
  lng: -99.08533448890726
};

// Opciones del mapa (personalización)
const mapOptions = {
  disableDefaultUI: false, // Mantener controles
  zoomControl: true,
  streetViewControl: false,
  mapTypeControl: false,
  fullscreenControl: true,
};

const Map = () => {
  return (
    <LoadScript 
      googleMapsApiKey={process.env.REACT_APP_GOOGLE_MAPS_API_KEY}
      loadingElement={
        <div style={{ height: '100%', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <div style={{ color: '#B32134', fontSize: '18px', fontWeight: '600' }}>
            Cargando mapa...
          </div>
        </div>
      }
    >
      <GoogleMap
        mapContainerStyle={mapStyles}
        zoom={15}
        center={defaultCenter}
        options={mapOptions}
      >
        {/* Marcador en la ubicación por defecto */}
        <Marker 
          position={defaultCenter}
          icon={{
            path: window.google?.maps?.SymbolPath?.CIRCLE || 0,
            fillColor: '#B32134',
            fillOpacity: 1,
            strokeColor: '#FFFFFF',
            strokeWeight: 3,
            scale: 10
          }}
        />
      </GoogleMap>
    </LoadScript>
  );
};

export default Map;
