// addlocationmodal.jsx
import React, { useState, useEffect } from 'react';
import './addlocationmodal.css';

const AddLocationModal = ({ isOpen, onClose }) => {
  const [formData, setFormData] = useState({
    nombre: '',
    direccion: '',
    colonia: '',
    delegacion: '',
    lat: '',
    lng: '',
    horario: '',
    dias: [],
    capacidad: '',
    tipo: '',
    requisitos: '',
    descripcion: '',
    foto: null
  });

  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const [map, setMap] = useState(null);
  const [marker, setMarker] = useState(null);

  const delegaciones = [
    '√Ålvaro Obreg√≥n', 'Azcapotzalco', 'Benito Ju√°rez', 'Coyoac√°n',
    'Cuajimalpa', 'Cuauht√©moc', 'Gustavo A. Madero', 'Iztacalco',
    'Iztapalapa', 'Magdalena Contreras', 'Miguel Hidalgo', 'Milpa Alta',
    'Tl√°huac', 'Tlalpan', 'Venustiano Carranza', 'Xochimilco'
  ];

  const diasSemana = ['Lunes', 'Martes', 'Mi√©rcoles', 'Jueves', 'Viernes', 'S√°bado', 'Domingo'];

  // Inicializar mapa cuando el modal se abre
  useEffect(() => {
    if (isOpen && window.google && !map) {
      setTimeout(() => {
        initMap();
      }, 300);
    }

    // Cleanup cuando se cierra
    return () => {
      if (marker) {
        marker.setMap(null);
      }
    };
  }, [isOpen]);

  const initMap = () => {
    const mapElement = document.getElementById('proposal-map');
    
    if (!mapElement) {
      console.error('‚ùå Elemento del mapa no encontrado');
      return;
    }

    try {
      const newMap = new window.google.maps.Map(mapElement, {
        center: { lat: 19.432608, lng: -99.133209 },
        zoom: 12,
        mapTypeControl: true,
        streetViewControl: false,
        fullscreenControl: true
      });

      // Evento de clic en el mapa
      newMap.addListener('click', (e) => {
        placeMarker(e.latLng, newMap);
      });

      setMap(newMap);
      console.log('‚úÖ Mapa inicializado correctamente');
    } catch (error) {
      console.error('‚ùå Error al inicializar mapa:', error);
    }
  };

  const placeMarker = (location, mapInstance) => {
    // Eliminar marcador anterior
    if (marker) {
      marker.setMap(null);
    }

    // Crear nuevo marcador
    const newMarker = new window.google.maps.Marker({
      position: location,
      map: mapInstance,
      draggable: true,
      animation: window.google.maps.Animation.DROP
    });

    // Actualizar coordenadas
    const lat = location.lat().toFixed(6);
    const lng = location.lng().toFixed(6);

    setFormData(prev => ({
      ...prev,
      lat: lat,
      lng: lng
    }));

    // Limpiar error
    if (errors.ubicacion) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors.ubicacion;
        return newErrors;
      });
    }

    // Evento al arrastrar
    newMarker.addListener('dragend', (e) => {
      setFormData(prev => ({
        ...prev,
        lat: e.latLng.lat().toFixed(6),
        lng: e.latLng.lng().toFixed(6)
      }));
    });

    setMarker(newMarker);
    console.log('üìç Marcador colocado:', lat, lng);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Limpiar error del campo
    if (errors[name]) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[name];
        return newErrors;
      });
    }
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Validar tipo
      if (!file.type.match(/image\/(jpeg|jpg|png)/)) {
        setErrors(prev => ({
          ...prev,
          foto: 'Solo se permiten im√°genes JPG, JPEG o PNG'
        }));
        return;
      }

      // Validar tama√±o
      if (file.size > 5000000) {
        setErrors(prev => ({
          ...prev,
          foto: 'La imagen no debe pesar m√°s de 5MB'
        }));
        return;
      }

      setFormData(prev => ({
        ...prev,
        foto: file
      }));

      if (errors.foto) {
        setErrors(prev => {
          const newErrors = { ...prev };
          delete newErrors.foto;
          return newErrors;
        });
      }
    }
  };

  const handleDiasChange = (dia) => {
    if (formData.dias.includes(dia)) {
      setFormData(prev => ({
        ...prev,
        dias: prev.dias.filter(d => d !== dia)
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        dias: [...prev.dias, dia]
      }));
    }

    if (errors.dias) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors.dias;
        return newErrors;
      });
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.nombre.trim()) newErrors.nombre = 'El nombre es obligatorio';
    if (!formData.direccion.trim()) newErrors.direccion = 'La direcci√≥n es obligatoria';
    if (!formData.colonia.trim()) newErrors.colonia = 'La colonia es obligatoria';
    if (!formData.delegacion) newErrors.delegacion = 'Selecciona una delegaci√≥n';
    if (!formData.horario.trim()) newErrors.horario = 'El horario es obligatorio';
    if (formData.dias.length === 0) newErrors.dias = 'Selecciona al menos un d√≠a';
    if (!formData.capacidad || formData.capacidad < 1) newErrors.capacidad = 'La capacidad debe ser mayor a 0';
    if (!formData.tipo) newErrors.tipo = 'Selecciona un tipo';
    if (!formData.lat || !formData.lng) newErrors.ubicacion = 'Selecciona una ubicaci√≥n en el mapa';
    if (!formData.foto) newErrors.foto = 'La foto es obligatoria';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setLoading(true);

    try {
      const token = localStorage.getItem('token');
      
      const submitData = new FormData();
      submitData.append('nombre', formData.nombre);
      submitData.append('direccion', formData.direccion);
      submitData.append('colonia', formData.colonia);
      submitData.append('delegacion', formData.delegacion);
      submitData.append('lat', formData.lat);
      submitData.append('lng', formData.lng);
      submitData.append('horario', formData.horario);
      submitData.append('dias', JSON.stringify(formData.dias));
      submitData.append('capacidad', formData.capacidad);
      submitData.append('tipo', formData.tipo);
      submitData.append('requisitos', formData.requisitos || 'Ninguno');
      submitData.append('descripcion', formData.descripcion || '');
      submitData.append('foto', formData.foto);

      const response = await fetch('http://localhost:5000/api/propuestas', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        },
        body: submitData
      });

      const data = await response.json();

      if (data.success) {
        setSuccess(true);
        setTimeout(() => {
          handleClose();
        }, 2500);
      } else {
        setErrors({ submit: data.message || 'Error al enviar la propuesta' });
      }

    } catch (error) {
      console.error('Error:', error);
      setErrors({ submit: 'Error al enviar la propuesta. Intenta de nuevo.' });
    } finally {
      setLoading(false);
    }
  };

  const handleClose = () => {
    // Limpiar marcador
    if (marker) {
      marker.setMap(null);
      setMarker(null);
    }

    // Limpiar mapa
    setMap(null);

    // Resetear form
    setFormData({
      nombre: '',
      direccion: '',
      colonia: '',
      delegacion: '',
      lat: '',
      lng: '',
      horario: '',
      dias: [],
      capacidad: '',
      tipo: '',
      requisitos: '',
      descripcion: '',
      foto: null
    });
    setErrors({});
    setSuccess(false);
    onClose();
  };

  if (!isOpen) return null;

  // Modal de √©xito
  if (success) {
    return (
      <div className="modal-overlay" onClick={handleClose}>
        <div className="modal-success" onClick={(e) => e.stopPropagation()}>
          <div className="success-icon">
            <svg width="64" height="64" viewBox="0 0 24 24" fill="none" stroke="#28a745" strokeWidth="2">
              <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"></path>
              <polyline points="22 4 12 14.01 9 11.01"></polyline>
            </svg>
          </div>
          <h2>¬°Propuesta Enviada!</h2>
          <p>Tu propuesta ha sido enviada exitosamente y ser√° revisada por un administrador.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="modal-overlay" onClick={handleClose}>
      <div className="add-bici-modal" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h2>Proponer Biciestacionamiento</h2>
          <button className="modal-close" onClick={handleClose}>
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <line x1="18" y1="6" x2="6" y2="18"></line>
              <line x1="6" y1="6" x2="18" y2="18"></line>
            </svg>
          </button>
        </div>

        <form className="add-bici-form" onSubmit={handleSubmit}>
          {errors.submit && (
            <div className="error-message">
              {errors.submit}
            </div>
          )}

          {/* Informaci√≥n b√°sica */}
          <div className="form-section">
            <h3>Informaci√≥n B√°sica</h3>
            
            <div className="form-group">
              <label htmlFor="nombre">Nombre *</label>
              <input
                type="text"
                id="nombre"
                name="nombre"
                placeholder="Ej: Biciestacionamiento Metro Chapultepec"
                value={formData.nombre}
                onChange={handleChange}
                disabled={loading}
              />
              {errors.nombre && <span className="error-text">{errors.nombre}</span>}
            </div>

            <div className="form-group">
              <label htmlFor="descripcion">Descripci√≥n</label>
              <textarea
                id="descripcion"
                name="descripcion"
                placeholder="Descripci√≥n del biciestacionamiento..."
                value={formData.descripcion}
                onChange={handleChange}
                rows="3"
                disabled={loading}
              />
            </div>
          </div>

          {/* Ubicaci√≥n */}
          <div className="form-section">
            <h3>Ubicaci√≥n</h3>
            
            <div className="form-group">
              <label htmlFor="direccion">Direcci√≥n *</label>
              <input
                type="text"
                id="direccion"
                name="direccion"
                placeholder="Ej: Av. Chapultepec 123"
                value={formData.direccion}
                onChange={handleChange}
                disabled={loading}
              />
              {errors.direccion && <span className="error-text">{errors.direccion}</span>}
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="delegacion">Delegaci√≥n *</label>
                <select
                  id="delegacion"
                  name="delegacion"
                  value={formData.delegacion}
                  onChange={handleChange}
                  disabled={loading}
                >
                  <option value="">Selecciona una delegaci√≥n</option>
                  {delegaciones.map(del => (
                    <option key={del} value={del}>{del}</option>
                  ))}
                </select>
                {errors.delegacion && <span className="error-text">{errors.delegacion}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="colonia">Colonia *</label>
                <input
                  type="text"
                  id="colonia"
                  name="colonia"
                  placeholder="Ej: Ju√°rez"
                  value={formData.colonia}
                  onChange={handleChange}
                  disabled={loading}
                />
                {errors.colonia && <span className="error-text">{errors.colonia}</span>}
              </div>
            </div>
          </div>

          {/* Mapa */}
          <div className="map-section">
            <h3>Ubicaci√≥n en el Mapa *</h3>
            <p className="map-hint">
              üëÜ Haz clic en el mapa para seleccionar la ubicaci√≥n exacta del biciestacionamiento
            </p>
            <div id="proposal-map" className="proposal-map"></div>
            
            {formData.lat && formData.lng && (
              <div className="coordinates-display">
                <span>üìç Latitud: {formData.lat}</span>
                <span>üìç Longitud: {formData.lng}</span>
              </div>
            )}
            {errors.ubicacion && <span className="error-text">{errors.ubicacion}</span>}
          </div>

          {/* Horarios y d√≠as */}
          <div className="form-section">
            <h3>Horario y Disponibilidad</h3>
            
            <div className="form-group">
              <label htmlFor="horario">Horario *</label>
              <input
                type="text"
                id="horario"
                name="horario"
                placeholder="Ej: 6:00 AM - 10:00 PM"
                value={formData.horario}
                onChange={handleChange}
                disabled={loading}
              />
              {errors.horario && <span className="error-text">{errors.horario}</span>}
            </div>

            <div className="form-group">
              <label>D√≠as Disponibles *</label>
              <div className="dias-grid">
                {diasSemana.map(dia => (
                  <label key={dia} className="dia-checkbox">
                    <input
                      type="checkbox"
                      checked={formData.dias.includes(dia)}
                      onChange={() => handleDiasChange(dia)}
                      disabled={loading}
                    />
                    <span>{dia}</span>
                  </label>
                ))}
              </div>
              {errors.dias && <span className="error-text">{errors.dias}</span>}
            </div>
          </div>

          {/* Capacidad y tipo */}
          <div className="form-section">
            <h3>Caracter√≠sticas</h3>
            
            <div className="form-row">
              <div className="form-group">
                <label htmlFor="capacidad">Capacidad *</label>
                <input
                  type="number"
                  id="capacidad"
                  name="capacidad"
                  placeholder="Ej: 20"
                  min="1"
                  value={formData.capacidad}
                  onChange={handleChange}
                  disabled={loading}
                />
                {errors.capacidad && <span className="error-text">{errors.capacidad}</span>}
              </div>

              <div className="form-group">
                <label htmlFor="tipo">Tipo *</label>
                <select
                  id="tipo"
                  name="tipo"
                  value={formData.tipo}
                  onChange={handleChange}
                  disabled={loading}
                >
                  <option value="">Selecciona un tipo</option>
                  <option value="P√∫blico">P√∫blico</option>
                  <option value="Privado">Privado</option>
                  <option value="Restringido">Restringido</option>
                </select>
                {errors.tipo && <span className="error-text">{errors.tipo}</span>}
              </div>
            </div>

            <div className="form-group">
              <label htmlFor="requisitos">Requisitos</label>
              <textarea
                id="requisitos"
                name="requisitos"
                placeholder="Ej: Credencial de estudiante, Candado propio, etc."
                value={formData.requisitos}
                onChange={handleChange}
                rows="2"
                disabled={loading}
              />
            </div>

            <div className="form-group">
              <label htmlFor="foto">Fotograf√≠a *</label>
              <input
                type="file"
                id="foto"
                accept="image/jpeg,image/jpg,image/png"
                onChange={handleFileChange}
                disabled={loading}
              />
              {formData.foto && <span className="file-name">‚úÖ {formData.foto.name}</span>}
              {errors.foto && <span className="error-text">{errors.foto}</span>}
            </div>
          </div>

          <div className="modal-actions">
            <button
              type="button"
              className="btn-cancel"
              onClick={handleClose}
              disabled={loading}
            >
              Cancelar
            </button>
            <button
              type="submit"
              className="btn-submit"
              disabled={loading}
            >
              {loading ? 'Enviando...' : 'Enviar Propuesta'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddLocationModal;
