// addlocationmodal.jsx
import React, { useState, useEffect } from 'react';
import './addlocationmodal.css';

const AddLocationModal = ({ isOpen, onClose }) => {
  const [formData, setFormData] = useState({
    nombre: '',
    direccion: '',
    colonia: '',
    delegacion: '',
    lat: '',
    lng: '',
    horario: '',
    dias: [],
    capacidad: '',
    tipo: '',
    requisitos: '',
    descripcion: '',
    foto: null
  });

  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const [mapLoaded, setMapLoaded] = useState(false);
  const [map, setMap] = useState(null);
  const [marker, setMarker] = useState(null);

  const delegaciones = [
    'Álvaro Obregón', 'Azcapotzalco', 'Benito Juárez', 'Coyoacán',
    'Cuajimalpa', 'Cuauhtémoc', 'Gustavo A. Madero', 'Iztacalco',
    'Iztapalapa', 'Magdalena Contreras', 'Miguel Hidalgo', 'Milpa Alta',
    'Tláhuac', 'Tlalpan', 'Venustiano Carranza', 'Xochimilco'
  ];

  const diasSemana = ['Lunes', 'Martes', 'Miércoles', 'Jueves', 'Viernes', 'Sábado', 'Domingo'];

  // Inicializar mapa cuando se abre el modal
  useEffect(() => {
    if (isOpen && !mapLoaded && window.google) {
      // Pequeño delay para asegurar que el DOM esté listo
      setTimeout(() => {
        initMap();
      }, 100);
    }
  }, [isOpen]);

  const initMap = () => {
    const mapElement = document.getElementById('proposal-map');
    if (!mapElement) {
      console.error('Elemento del mapa no encontrado');
      return;
    }

    try {
      const newMap = new window.google.maps.Map(mapElement, {
        center: { lat: 19.445360, lng: -99.152241 },
        zoom: 13,
        mapTypeControl: false,
        streetViewControl: false,
        fullscreenControl: false
      });

      // Agregar evento de clic en el mapa
      newMap.addListener('click', (e) => {
        placeMarker(e.latLng, newMap);
      });

      setMap(newMap);
      setMapLoaded(true);
    } catch (error) {
      console.error('Error al inicializar mapa:', error);
    }
  };

  const placeMarker = (location, mapInstance) => {
    // Eliminar marcador anterior si existe
    if (marker) {
      marker.setMap(null);
    }

    // Crear nuevo marcador
    const newMarker = new window.google.maps.Marker({
      position: location,
      map: mapInstance,
      draggable: true,
      animation: window.google.maps.Animation.DROP
    });

    // Actualizar coordenadas en el formulario
    setFormData(prev => ({
      ...prev,
      lat: location.lat().toFixed(6),
      lng: location.lng().toFixed(6)
    }));

    // Limpiar error de ubicación si existía
    if (errors.ubicacion) {
      setErrors(prev => ({
        ...prev,
        ubicacion: ''
      }));
    }

    // Evento cuando se arrastra el marcador
    newMarker.addListener('dragend', (e) => {
      setFormData(prev => ({
        ...prev,
        lat: e.latLng.lat().toFixed(6),
        lng: e.latLng.lng().toFixed(6)
      }));
    });

    setMarker(newMarker);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    // Limpiar error del campo
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Validar tipo
      const validTypes = ['image/jpeg', 'image/jpg', 'image/png'];
      if (!validTypes.includes(file.type)) {
        setErrors(prev => ({
          ...prev,
          foto: 'Solo se permiten imágenes JPG, JPEG o PNG'
        }));
        return;
      }

      // Validar tamaño (máx 5MB)
      if (file.size > 5000000) {
        setErrors(prev => ({
          ...prev,
          foto: 'La imagen no debe pesar más de 5MB'
        }));
        return;
      }

      setFormData(prev => ({
        ...prev,
        foto: file
      }));
      setErrors(prev => ({
        ...prev,
        foto: ''
      }));
    }
  };

  const handleDiaToggle = (dia) => {
    setFormData(prev => ({
      ...prev,
      dias: prev.dias.includes(dia)
        ? prev.dias.filter(d => d !== dia)
        : [...prev.dias, dia]
    }));

    // Limpiar error de días
    if (errors.dias) {
      setErrors(prev => ({
        ...prev,
        dias: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.nombre.trim()) newErrors.nombre = 'El nombre es obligatorio';
    if (!formData.direccion.trim()) newErrors.direccion = 'La dirección es obligatoria';
    if (!formData.colonia.trim()) newErrors.colonia = 'La colonia es obligatoria';
    if (!formData.delegacion) newErrors.delegacion = 'Selecciona una delegación';
    if (!formData.lat || !formData.lng) newErrors.ubicacion = 'Selecciona una ubicación en el mapa';
    if (!formData.horario.trim()) newErrors.horario = 'El horario es obligatorio';
    if (formData.dias.length === 0) newErrors.dias = 'Selecciona al menos un día';
    if (!formData.capacidad || formData.capacidad < 1) newErrors.capacidad = 'La capacidad debe ser mayor a 0';
    if (!formData.tipo) newErrors.tipo = 'Selecciona un tipo';
    if (!formData.foto) newErrors.foto = 'La foto es obligatoria';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setLoading(true);

    try {
      const token = localStorage.getItem('token');
      
      const submitData = new FormData();
      submitData.append('nombre', formData.nombre);
      submitData.append('direccion', formData.direccion);
      submitData.append('colonia', formData.colonia);
      submitData.append('delegacion', formData.delegacion);
      submitData.append('lat', formData.lat);
      submitData.append('lng', formData.lng);
      submitData.append('horario', formData.horario);
      submitData.append('dias', JSON.stringify(formData.dias));
      submitData.append('capacidad', formData.capacidad);
      submitData.append('tipo', formData.tipo);
      submitData.append('requisitos', formData.requisitos || 'Ninguno');
      submitData.append('descripcion', formData.descripcion || '');
      submitData.append('foto', formData.foto);

      const response = await fetch('http://localhost:5000/api/propuestas', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        },
        body: submitData
      });

      const data = await response.json();

      if (data.success) {
        setSuccess(true);
        setTimeout(() => {
          handleClose();
        }, 2000);
      } else {
        setErrors({ submit: data.message || 'Error al enviar la propuesta' });
      }

    } catch (error) {
      console.error('Error:', error);
      setErrors({ submit: 'Error al enviar la propuesta. Intenta de nuevo.' });
    } finally {
      setLoading(false);
    }
  };

  const handleClose = () => {
    setFormData({
      nombre: '',
      direccion: '',
      colonia: '',
      delegacion: '',
      lat: '',
      lng: '',
      horario: '',
      dias: [],
      capacidad: '',
      tipo: '',
      requisitos: '',
      descripcion: '',
      foto: null
    });
    setErrors({});
    setSuccess(false);
    setMapLoaded(false);
    if (marker) marker.setMap(null);
    setMarker(null);
    setMap(null);
    onClose();
  };

  if (!isOpen) return null;

  if (success) {
    return (
      <div className="modal-overlay" onClick={handleClose}>
        <div className="modal-success" onClick={(e) => e.stopPropagation()}>
          <div className="success-icon">
            <svg width="64" height="64" viewBox="0 0 24 24" fill="none" stroke="#28a745" strokeWidth="2">
              <path d="M22 11.08V12a10 10 0 1 1-5.93-9.14"></path>
              <polyline points="22 4 12 14.01 9 11.01"></polyline>
            </svg>
          </div>
          <h2>¡Propuesta Enviada!</h2>
          <p>Tu propuesta ha sido enviada exitosamente. Será revisada por un administrador.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="modal-overlay" onClick={handleClose}>
      <div className="modal-container" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h2>Proponer Biciestacionamiento</h2>
          <button className="modal-close" onClick={handleClose}>
            <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <line x1="18" y1="6" x2="6" y2="18"></line>
              <line x1="6" y1="6" x2="18" y2="18"></line>
            </svg>
          </button>
        </div>

        <form className="modal-form" onSubmit={handleSubmit}>
          <div className="form-section">
            <h3>Información Básica</h3>
            
            <div className="form-group">
              <label>Nombre del Biciestacionamiento *</label>
              <input
                type="text"
                name="nombre"
                value={formData.nombre}
                onChange={handleChange}
                placeholder="Ej: Biciestacionamiento Metro Chapultepec"
                className={errors.nombre ? 'error' : ''}
              />
              {errors.nombre && <span className="error-message">{errors.nombre}</span>}
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>Dirección *</label>
                <input
                  type="text"
                  name="direccion"
                  value={formData.direccion}
                  onChange={handleChange}
                  placeholder="Ej: Av. Chapultepec 123"
                  className={errors.direccion ? 'error' : ''}
                />
                {errors.direccion && <span className="error-message">{errors.direccion}</span>}
              </div>

              <div className="form-group">
                <label>Colonia *</label>
                <input
                  type="text"
                  name="colonia"
                  value={formData.colonia}
                  onChange={handleChange}
                  placeholder="Ej: Juárez"
                  className={errors.colonia ? 'error' : ''}
                />
                {errors.colonia && <span className="error-message">{errors.colonia}</span>}
              </div>
            </div>

            <div className="form-group">
              <label>Delegación *</label>
              <select
                name="delegacion"
                value={formData.delegacion}
                onChange={handleChange}
                className={errors.delegacion ? 'error' : ''}
              >
                <option value="">Selecciona una delegación</option>
                {delegaciones.map(del => (
                  <option key={del} value={del}>{del}</option>
                ))}
              </select>
              {errors.delegacion && <span className="error-message">{errors.delegacion}</span>}
            </div>
          </div>

          <div className="form-section">
            <h3>Ubicación en el Mapa *</h3>
            <p className="hint">Haz clic en el mapa para seleccionar la ubicación exacta</p>
            <div id="proposal-map" className="proposal-map"></div>
            {formData.lat && formData.lng && (
              <div className="coordinates-display">
                <span>📍 Lat: {formData.lat}</span>
                <span>📍 Lng: {formData.lng}</span>
              </div>
            )}
            {errors.ubicacion && <span className="error-message">{errors.ubicacion}</span>}
          </div>

          <div className="form-section">
            <h3>Horario y Disponibilidad</h3>
            
            <div className="form-group">
              <label>Horario *</label>
              <input
                type="text"
                name="horario"
                value={formData.horario}
                onChange={handleChange}
                placeholder="Ej: 6:00 AM - 10:00 PM"
                className={errors.horario ? 'error' : ''}
              />
              {errors.horario && <span className="error-message">{errors.horario}</span>}
            </div>

            <div className="form-group">
              <label>Días Disponibles *</label>
              <div className="dias-grid">
                {diasSemana.map(dia => (
                  <button
                    key={dia}
                    type="button"
                    className={`dia-chip ${formData.dias.includes(dia) ? 'active' : ''}`}
                    onClick={() => handleDiaToggle(dia)}
                  >
                    {dia.substring(0, 3)}
                  </button>
                ))}
              </div>
              {errors.dias && <span className="error-message">{errors.dias}</span>}
            </div>
          </div>

          <div className="form-section">
            <h3>Características</h3>
            
            <div className="form-row">
              <div className="form-group">
                <label>Capacidad (espacios) *</label>
                <input
                  type="number"
                  name="capacidad"
                  value={formData.capacidad}
                  onChange={handleChange}
                  min="1"
                  placeholder="Ej: 20"
                  className={errors.capacidad ? 'error' : ''}
                />
                {errors.capacidad && <span className="error-message">{errors.capacidad}</span>}
              </div>

              <div className="form-group">
                <label>Tipo de Acceso *</label>
                <select
                  name="tipo"
                  value={formData.tipo}
                  onChange={handleChange}
                  className={errors.tipo ? 'error' : ''}
                >
                  <option value="">Selecciona un tipo</option>
                  <option value="Público">Público</option>
                  <option value="Privado">Privado</option>
                  <option value="Restringido">Restringido</option>
                </select>
                {errors.tipo && <span className="error-message">{errors.tipo}</span>}
              </div>
            </div>

            <div className="form-group">
              <label>Requisitos (opcional)</label>
              <input
                type="text"
                name="requisitos"
                value={formData.requisitos}
                onChange={handleChange}
                placeholder="Ej: Credencial de estudiante, Registro previo"
              />
            </div>

            <div className="form-group">
              <label>Descripción (opcional)</label>
              <textarea
                name="descripcion"
                value={formData.descripcion}
                onChange={handleChange}
                rows="3"
                placeholder="Información adicional sobre el biciestacionamiento"
              />
            </div>
          </div>

          <div className="form-section">
            <h3>Fotografía *</h3>
            <div className="file-upload">
              <input
                type="file"
                id="foto"
                accept="image/jpeg,image/jpg,image/png"
                onChange={handleFileChange}
                className={errors.foto ? 'error' : ''}
              />
              <label htmlFor="foto" className="file-label">
                <svg width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <rect x="3" y="3" width="18" height="18" rx="2" ry="2"></rect>
                  <circle cx="8.5" cy="8.5" r="1.5"></circle>
                  <polyline points="21 15 16 10 5 21"></polyline>
                </svg>
                <span>{formData.foto ? formData.foto.name : 'Seleccionar imagen (máx 5MB)'}</span>
              </label>
              {errors.foto && <span className="error-message">{errors.foto}</span>}
            </div>
          </div>

          {errors.submit && (
            <div className="error-banner">
              <svg width="20" height="20" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <circle cx="12" cy="12" r="10"></circle>
                <line x1="12" y1="8" x2="12" y2="12"></line>
                <line x1="12" y1="16" x2="12.01" y2="16"></line>
              </svg>
              <span>{errors.submit}</span>
            </div>
          )}

          <div className="modal-footer">
            <button type="button" className="btn-cancel" onClick={handleClose} disabled={loading}>
              Cancelar
            </button>
            <button type="submit" className="btn-submit" disabled={loading}>
              {loading ? 'Enviando...' : 'Enviar Propuesta'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddLocationModal;
