// Home.jsx
import React, { useState, useEffect } from 'react';
import Navbar from './components/navbar.jsx';
import SearchBar from './components/searchbar.jsx';
import FilterButton from './components/filterbutton.jsx';
import Map from './map.jsx';
import AddLocationButton from './components/addlocationbutton.jsx';
import AddLocationModal from './components/addlocationmodal.jsx';
import './main.css';

const Home = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Simular carga inicial
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 1500); // Espera 1.5 segundos para que el mapa cargue

    return () => clearTimeout(timer);
  }, []);

  const handleSearch = (searchTerm) => {
    console.log('Buscando:', searchTerm);
  };

  const handleFilter = () => {
    console.log('Abrir filtros');
  };

  const handleAddLocation = () => {
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  if (isLoading) {
    return (
      <div className="loading-screen">
        <div className="loading-content">
          <div className="spinner"></div>
          <h2>Cargando Mapa</h2>
          <p>Preparando tu experiencia...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="home-container">
      <Navbar />
      
      <div className="search-section">
        <SearchBar onSearch={handleSearch} />
        <FilterButton onClick={handleFilter} />
      </div>

      <div className="map-container">
        <Map />
      </div>

      <AddLocationButton onClick={handleAddLocation} />
      
      <AddLocationModal 
        isOpen={isModalOpen} 
        onClose={handleCloseModal} 
      />
    </div>
  );
};

export default Home;
